import {createSlice} from "@reduxjs/toolkit";


const ToDoItemsSlice = createSlice({
    name:"toDoItems",
    initialState:{
        toDoItems:[],
       
    },

    reducers:{
        UpdateToDoItem:(state,action)=>{
           console.log("UpdateToDoItem");
         
        },
        InitToDoItems:(state,action)=>{
           
            console.log("InitToDoItems");
           
            var items = action.payload;
            console.log(items);
            
            for(var el in items)
            {
              state.toDoItems.push(el);
            }

           console.log(state.toDoItems);
         }
    },
});

export const {UpdateToDoItem,InitToDoItems} = ToDoItemsSlice.actions
export default ToDoItemsSlice.reducer
