import React, { useState,useEffect } from 'react'
import styled from 'styled-components'
import Announcement from '../components/Announcement'
import Board from '../components/Board';
import Footer from '../components/Footer'
import TaskList from '../components/TaskList';
import { useSelector,useDispatch } from "react-redux";
import { InitToDoItems } from '../redux/toDoItemsReducer';

const Container = styled.div`
display:flex;flex-direction:column;
`;

export const Home = () => {
  
    const dispatch = useDispatch();

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [toDoItems,setToDoItems] = useState([]);

  const initStatusForToDoItems =(items)=>{
    items.map(el=>{

            if(el.completed)
            {
                    el.status=2;
            }
            if(!el.completed)
            {
                    el.status=0;
            }
           
        })

        return items;
  }

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/todos")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);

          var items = initStatusForToDoItems(result);
            console.log("dispatch");
            setToDoItems(items);
          dispatch(InitToDoItems({...toDoItems}));
      },
       
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])

  if (error) {
    return <div>Erreur : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Chargement...</div>;
  } else {
    return (
      
        <Container>
            <Announcement />
           
              <TaskList todoList={toDoItems}/>
              <Board    todoList={toDoItems}/>
              <Footer/>
        </Container>
)
  }

   
}
export default Home
