import React from 'react'
import styled from 'styled-components'
import { mobile } from '../responsive';
import {todoList} from '../data/data';
import TodoItem from '../components/TodoItem';

const Container = styled.div`padding:20px;
display:flex;
flex-direction:column;
height:40vh;background:red;
${mobile({padding:"1px"})}`;

const Title = styled.h1`margin-bottom:10px;`;
const List = styled.div`display:flex;border: solid 0.5px blue;overflow:auto;`;
const ListToDo = styled.div`flex:1;display:flex;flex-direction:column;overflow:auto;`;
const ListInProgress = styled.div`flex:1;display:flex;flex-direction:column;overflow:auto;`;
const ListDone = styled.div`flex:1;display:flex;flex-direction:column;overflow:auto;`;
const Status = styled.span`font-weight:bold;justify-content:center;font-size:500;flex:1;`;
const Header = styled.div`flex:3;display:flex;align-items:center;`;
function Board() {
    return ( 
        <Container>
               <Title>Boards</Title>
               <Header>
                   <Status>To Do</Status> 
                    <Status>In Progress</Status>
                    <Status>Done</Status>
                    </Header>
               <List>
                   <ListToDo>
                     
                       {todoList.map(item =>(
                <TodoItem key={item.id} item={item}/>
                                ) )}
                   </ListToDo>
                   <ListInProgress>
                     
                       {todoList.map(item =>(
                <TodoItem key={item.id} item={item}/>
                                ) )}
                   </ListInProgress>
                   <ListDone>
                       
                       {todoList.map(item =>(
                <TodoItem key={item.id} item={item}/>
                                ) )}
                    </ListDone>
               </List>
        </Container>
    )
}

export default Board
