import React, { useState,useEffect } from 'react'
import styled from 'styled-components'
import Announcement from '../components/Announcement'
import Board from '../components/Board';
import Footer from '../components/Footer'
import TaskList from '../components/TaskList';
import { publicRequest } from '../requestMethode';
import { useSelector,useDispatch } from "react-redux";
import { InitToDoItems } from '../redux/toDoItemsReducer';

const Container = styled.div`
display:flex;flex-direction:column;
`;

export const Home = () => {
  
    const dispatch = useDispatch();

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [toDoItems,setToDoItems] = useState([]);
  const toDoItemsFromState = useSelector(state=>state.toDoItems);
  const initStatusForToDoItems =(items)=>{
    items.map(el=>{

            if(el.completed)
            {
                    el.status=2;
            }
            if(!el.completed)
            {
                    el.status=0;
            }
           
        })

        return items;
  }


  useEffect(()=>{

    const getToDoItems = async ()=>{
        try{
            const res  = await publicRequest.get(`todos`);
            console.log(res.data);
            setIsLoaded(true);

            var items = initStatusForToDoItems(res.data);
           
              setToDoItems(items);

            dispatch(InitToDoItems({...toDoItems}));
               
            }
            catch(error)
            {
                setIsLoaded(true);
                setError(error);
            }}
            getToDoItems(); 


},[]);

  if (error) {
    return <div>Erreur : {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Chargement...</div>;
  } else {
    return (
      
        <Container>
            <Announcement />
           
              <TaskList todoList={toDoItems}/>
              <Board    todoList={toDoItems}/>
              <Footer/>
        </Container>
)
  }

   
}
export default Home
