import {createSlice} from "@reduxjs/toolkit";


const ToDoItemsSlice = createSlice({
    name:"ToDoItems",
    initialState:{
        toDoItems:[],
       
    },

    reducers:{
        UpdateToDoItem:(state,action)=>{
           console.log("UpdateToDoItem");
           console.log(state);
           console.log(action);
        }
    },
});

export const {GetToDoItems} = ToDoItemsSlice.actions
export default ToDoItemsSlice.reducer
