import React from 'react'
import styled from 'styled-components'
import { mobile } from '../responsive';
import TodoItem from './TodoItem';
import { useSelector,useDispatch } from "react-redux";

const Container = styled.div`padding:20px;
display:flex;
flex-direction:column;
height:40vh;
${mobile({padding:"1px"})}`;

const Title = styled.h1`margin-bottom:10px;`;
const List = styled.div`display:flex;border: solid 0.5px blue;overflow:auto;${mobile({flexDirection:"column"})}`;
const ListToDo = styled.div`flex:1;display:flex;flex-direction:column;overflow:auto;background-color:#e8a8b496;margin:5px;`;
const ListInProgress = styled.div`flex:1;display:flex;flex-direction:column;overflow:auto;background-color:#93a0ea;margin:5px;`;
const ListDone = styled.div`flex:1;display:flex;flex-direction:column;overflow:auto;background-color:#a5fee7;margin:5px;`;
const Status = styled.span`font-weight:bold;text-align:center;flex:1;height:30px;padding:10px;`;

function Board() {

    const toDoItems = useSelector(state=>state.toDoItems.toDoItems);
   
    return ( 
        <Container>
               <Title>Boards</Title>
            
               <List>
              
                   <ListToDo>
                   <Status>To Do</Status> 
                       {toDoItems.filter(el=>el.status===0).map(item =>(
                <TodoItem key={item.id} item={item}/>
                                ) )}
                   </ListToDo>
                   <ListInProgress>
                   <Status>In Progress</Status>
                       {toDoItems.filter(el=>el.status===1).map(item =>(
                <TodoItem key={item.id} item={item}/>
                                ) )}
                   </ListInProgress>
                   <ListDone>
                   <Status>Done</Status>
                       {toDoItems.filter(el=>el.status===2).map(item =>(
                <TodoItem key={item.id} item={item}/>
                                ) )}
                    </ListDone>
               </List>
        </Container>
    )
}

export default Board
