import {configureStore} from "@reduxjs/toolkit";
import taskReducer from "./tasksReux";
import toDoItemsReducer from "./toDoItemsReducer"

export default configureStore({
    reducer:{
        task:taskReducer,
        toDoItems:toDoItemsReducer
    }
})
