import React from 'react'
import styled from 'styled-components'
import { mobile } from '../responsive';


const Container = styled.div`padding:20px;
display:flex;
flex-wrap:wrap;
flex-direction:column;
height:40vh;background:red;
${mobile({padding:"0px",flexDirection:"column"})}`;

const Title = styled.h1`margin-bottom:10px;`;
const List = styled.div`display:flex;border : solid 0.5px blue;`;
const ListToDo = styled.div`flex:1;display:flex;flex-direction:column;`;
const ListInProgress = styled.div`flex:1;display:flex;flex-direction:column;`;
const ListDone = styled.div`flex:1;display:flex;flex-direction:column;flex:1;display:flex;flex-direction:column;`;

function Board() {
    return ( 
        <Container>
               <Title>Boards</Title>
               <List>
                   <ListToDo>ToDo</ListToDo>
                   <ListInProgress>In Progress</ListInProgress>
                   <ListDone>Done</ListDone>
               </List>
        </Container>
    )
}

export default Board
