import React from 'react'
import styled from 'styled-components'
const Container = styled.div`
display:flex;padding:2px;
border:solid;
margin-bottom:2px;
`;
const assignPicture = styled.div`width:40px;height:40px;border-radius:50%;
color:white;background-color:#${props=>props.color};
display:flex;justify-content:center;align-items:center;marign-right:20px;`;

const TodoItem = ({item}) => {
    return (
        <Container>
            <assignPicture/> {item.title}
        </Container>
    )
}

export default TodoItem
