import React from 'react'
import styled from 'styled-components'
import TodoItem
 from './TodoItem';
const ListToDo = styled.div`flex:1;display:flex;flex-direction:column;overflow:auto;background-color:#e8a8b496;margin:5px;`;
const Status = styled.span`font-weight:bold;text-align:center;flex:1;height:30px;padding:10px;`;

const ColumnListItems = ({items,status,title}) => {
    return (
        <ListToDo>
        <Status>{title}</Status> 
            {items.filter(el=>el.status===status).map(item =>(
     <TodoItem key={item.id} item={item}/>
                     ) )}
        </ListToDo>
    )
}

export default ColumnListItem
