import {createSlice} from "@reduxjs/toolkit";

const taskSlice = createSlice({
    name:"task",
    initialState:{
        task:{},
       
    },

    reducers:{
        UpdateTaskStatus:(state,action)=>{
           if(state.status<2)
           {
                state+=1;
           }
           
        }
    },
});

export const {UpdateTaskStatus} = taskSlice.actions
export default taskSlice.reducer
